items = [{"title" =>"Chromecast", "category_name" =>"Electronics", "price"=>35,"link"=>"http://www.amazon.com/Google-Chromecast-Streaming-Media-Player/dp/B00DR0PDNE","description"=>"Stream online video, music to your TV using your smartphone, tablet, or laptop; Available for Windows computers running Windows 7 or higher.","image_url"=>"http://ecx.images-amazon.com/images/I/811nvG%2BLgML._SL1500_.jpg", "merchant_name"=>"Amazon"}, {"title" =>"Phoenix Wright: Ace Attorney", "price"=>25.99,"link"=>"http://www.amazon.com/Phoenix-Wright-Ace-Attorney-Nintendo-DS/dp/B000B69E96","description"=>"Not everyone is innocent until proven guilty! Players star as a defense attorney, who must prove his seemingly guilty client's innocence no matter how dire the circumstances may seem. The game presents twisting storylines and intriguing gameplay in a comical anime style. Players must collect evidence, weed through inconsistent testimonies, and overcome corrupt agendas to ensure that justice prevails.","image_url"=>"http://ecx.images-amazon.com/images/I/61D1Q6W2G9L._SX385_.jpg","category_name" =>"Video Games", "merchant_name"=>"Amazon"}, {"title" =>"Chess in Concert","category_name" =>"DVDs", "price"=>14.98,"link"=>"http://www.amazon.com/dp/B0028SVXVM","description"=>"Starring multiplatinum artist Josh Groban, Tony-winning Idina Menzel (Wicked) and Tony nominated Adam Pascal (RENT), Chess In Concert revives the eclectic yet wonderfully pop 1984 concept album featuring the music of ABBA's Björn Ulvaeus and Benny Andersson and lyrics of Tim Rice (Jesus Christ Superstar, The Lion King, Evita). Recorded at London's Royal Albert Hall in May 2008, this spectacular Chess In Concert - introduced by Rice and with the 50- piece City of London Philharmonic and 100-voice West End Chorus - finally fulfills the promise of the popular cult musical.","image_url"=>"http://ecx.images-amazon.com/images/I/41GsaEvM0OL._SY300_.jpg", "merchant_name"=>"Amazon"}, {"title" =>"Dr. Horrible's Sing-Along Blog", "category_name" =>"DVDs", "price"=>5.00,"link"=>"http://www.amazon.com/dp/B001M5UDGS","description"=>"Neil Patrick Harris (How I Met Your Mother) stars as Billy, A.K.A. Dr. Horrible, a budding super-villain whose plans for world domination continually go awry.  His two goals: getting accepted into the Evil League of Evil, and working up the guts to speak to his laundromat crush Penny, played by Felicia Day (The Guild).   The only thing standing in his way is Captain Hammer, Billy's superhero arch-nemesis played by Nathan Fillion (Firefly).  With one big score, Billy could get into the E.L.E. and earn the respect of Penny, but only if he can keep her away from the dashing Captain Hammer...","image_url"=>"http://ecx.images-amazon.com/images/I/512IePeoDvL._SY300_.jpg", "merchant_name"=>"Amazon"}, {"title" =>"Divergent (Divergent Series)","category_name" =>"Books", "price"=>4.99,"link"=>"http://www.amazon.com/Divergent-Veronica-Roth-ebook/dp/B004CFA9RS","description"=>"Gr 9 Up-In the dystopian Chicago setting of Roth's novel (Katherine Tegen Bks., 2011), the population is divided into five factions. Upon declaring allegiance to one of them, 16-year-old Beatrice will decide her future. Beatrice and her brother, Caleb, grew up in helpful, unassuming Abnegation, always putting others first. During her aptitude testing, a simulation probes her suitability for Abnegation, Amity, Candor, Dauntless, and Erudite. Rather than getting a clear reading of her strengths, Beatrice's result is disturbing and dangerous: she is Divergent.","image_url"=>"http://ecx.images-amazon.com/images/I/41vx3ZStnuL._BO2,204,203,200_PIsitb-sticker-v3-big,TopRight,0,-55_SX278_SY278_PIkin4,BottomRight,1,22_AA300_SH20_OU01_.jpg", "merchant_name"=>"Amazon"}, {"title" =>"The Fault in Our Stars", "category_name" =>"Books", "price"=>7.99,"link"=>"http://www.amazon.com/The-Fault-Stars-John-Green-ebook/dp/B005ZOBNOI","description"=>"Despite the tumor-shrinking medical miracle that has bought her a few years, Hazel has never been anything but terminal, her final chapter inscribed upon diagnosis. But when a gorgeous plot twist named Augustus Waters suddenly appears at Cancer Kid Support Group, Hazel’s story is about to be completely rewritten.","image_url"=>"http://ecx.images-amazon.com/images/I/51e1dY1RXCL._BO2,204,203,200_PIsitb-sticker-v3-big,TopRight,0,-55_SX278_SY278_PIkin4,BottomRight,1,22_AA300_SH20_OU01_.jpg", "merchant_name"=>"Amazon"}, {"title" =>"Elder Scrolls V: Skyrim", "category_name" =>"Video Games", "price"=>19.96,"link"=>"http://www.amazon.com/Elder-Scrolls-V-Skyrim-Xbox-360/dp/B004HYK956","description"=>"Skyrim reimagines the open-world fantasy epic, pushing the game play and technology of a virtual world to new heights","image_url"=>"http://ecx.images-amazon.com/images/I/91EWvx3TB4L._SL1500_.jpg", "merchant_name"=>"Amazon"}, {"title" =>"You're My Wonder Walrus Wall Decor", "price"=>79.99, "link"=>"http://www.modcloth.com/shop/wall-decor/you-re-my-wonder-walrus-wall-decor","description"=>"Today is going to be the day your home decor goes from awesome to wildly wonderful! Hang this white walrus head in your den, dining room, or dorm to instantly add a kick of quirk to your personal oasis.", "category_name" =>"Decor", "image_url"=>"http://productshots3.modcloth.net/productshots/0139/6685/6d89c09ded4ad5b26342ba7bccf49922.jpg?1393528137", "merchant_name"=>"ModCloth"}, {"title" =>"Dots to Discuss Scarf in Mint", "price"=>24.99,"link"=>"http://www.modcloth.com/shop/scarves/dots-to-discuss-scarf-in-mint", "category_name" =>"Accessories", "description"=>"Your weekly coffee date with your BFF is the favorite thing on your social calendar! To keep yourself cozy and comfortable in the coffee shop, you’ve wrapped this polka-dotted scarf around your neck and shoulders. A sheer minty green swathe with grape-hues spots, this scarf makes you feel like an old-school movie star, especially with your classic combination of a crisp Oxford top, skinnies, and patent pumps!","im